    //       </Grid>

    //       <Grid item>
    //         <Typography variant='body1' style={{ userSelect: 'all' }}>
    //           {uuid}
    //         </Typography>
    //       </Grid>

          <div className="App">
            <h1>{message}</h1>
          </div>

          /* <Grid item>
            <Button onClick={generateUuid} variant='contained' color='primary'>
              Generate new UUID
            </Button>
          </Grid>
        </Grid>
      </Box>
    </ThemeProvider> */
  );
}

export default App;
