import { Button, createTheme, Grid, ThemeProvider, Typography, Box } from '@material-ui/core';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import { useMemo, useState, useEffect } from 'react';

// Define the shape of the message array elements
interface Chapter {
  chapterInfo: string;
  chapterLink: string;
}

function App() {
  const prefersDarkMode = useMediaQuery('(prefers-color-scheme: dark)');

  // Explicitly type the message state as an array of Chapter objects
  const [message, setMessage] = useState<Chapter[]>([]);

  useEffect(() => {
    fetch("http://localhost:8000/message")
      .then((res) => res.json())
      .then((data) => setMessage(data.message || [])) // Ensure data is an array
      .catch(err => console.error("Error fetching message:", err));
  }, []);

  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          type: prefersDarkMode ? 'dark' : 'light', // Use 'type' for Material-UI v4
        },
        typography: {
          h1: {
            fontSize: 16,
            fontWeight: 400,
          },
          body1: {
            fontFamily: 'monospace',
            fontSize: 18,
          },
        },
      }),
    [prefersDarkMode]
  );

  return (
    <ThemeProvider theme={theme}>
      <Box component='main' padding={2} display='flex'>
        <Grid container direction='column' justifyContent='center' alignItems='center' spacing={2}>
          <Grid item>
            <Typography variant='h1'>Fetched Data</Typography>
          </Grid>

          <Grid item>
            {message.length > 0 ? (
              message.map((chapter, index) => (
                <Typography key={index} variant='body1'>
                  {chapter.chapterInfo} - <a href={`https://mangafire.to${chapter.chapterLink}`} target="_blank" rel="noopener noreferrer">Read</a>
                </Typography>
              ))
            ) : (
              <Typography variant='body1'>No data available</Typography>
            )}
          </Grid>
        </Grid>
      </Box>
    </ThemeProvider>
  );
}

export default App;
